import {IRoute} from "./IRoute";
import {ICompetition} from "./ICompetition";
import {isNullOrUndefined} from "util";


var pdfMake = require('pdfmake/build/pdfmake.js');
var pdfFonts = require('pdfmake/build/vfs_fonts.js');

export function generateScorecard(routes:IRoute[], competition:ICompetition) {
    const {vfs} = pdfFonts.pdfMake;
    pdfMake.vfs = vfs;

    const docDefinition = {
        content: [
            {
                columns: [
                    {
                        stack: [
                            { text: competition.compName, style: 'header', margin: [ 0, 0, 0, 20 ]},
                            { text: "Name:____________________________",  margin: [ 0, 0, 0, 10 ]},
                            { columns: [
                                    "Category (circle one):",
                                    {
                                        stack: [
                                            { text: "Beginner(V0-V2)", margin: [ 0, 0, 0, 5 ]},
                                            { text: "Advanced(V6+)", margin: [ 0, 0, 0, 5 ]}
                                        ]
                                    },
                                    {
                                        stack: [
                                            { text: "Intermediate(V3-V5)", margin: [ 0, 0, 0, 5 ]},
                                            { text: "CCS", margin: [ 0, 0, 0, 5 ]}
                                        ]
                                    }]
                            },
                            { text: "CCS Number:__________________________",  margin: [ 0, 0, 0, 10 ]},
                            { text: "Gender:     Male          Female",  margin: [ 0, 0, 0, 3 ]},
                        ],
                        style: "info",
                        width: "*"
                    },
                    {
                        image: image,
                        fit: [150, 150],
                        width: '150',
                    }
                ],
                // optional space between columns
                columnGap: 10
            },
            {
                table: {
                    // headers are automatically repeated if the table spans over multiple pages
                    // you can declare how many rows should be treated as headers
                    headerRows: 1,
                    widths: getPDFColumns(competition).map(col => "*"),
                    body: [
                        getPDFColumns(competition),
                        ...convertDataToString(routes, competition)
                    ]
                },
                style: 'center'
            }
        ],

        styles: {
            header: {
                fontSize: 14,
                bold: true
            },
            info: {
                fontSize: 10
            },
            center: {
                fontSize: 6,
                alignment: 'center'
            }
        }
    };
    pdfMake.createPdf(docDefinition).open();
}

function getPDFColumns(competition:ICompetition) {
    const colData = ['#', 'Points'];
    if (competition.showName) {
        colData.push('Name');
    }
    if (competition.showSetter) {
        colData.push('Setter');

    }
    if (competition.showLocation) {
        colData.push('Location');

    }
    colData.push("Attempts");
    colData.push("Witness 1");
    colData.push("Witness 2");

    return colData;
}

function convertDataToString(routes:IRoute[], competition:ICompetition) {
    return routes.map(route => {
        const routeData = [route.routeId, route.points];
        if (competition.showName) {
            routeData.push(isNullOrUndefined(route.name) ? "" : route.name);
        }
        if (competition.showSetter) {
            routeData.push(isNullOrUndefined(route.setter) ? "" : route.setter);

        }
        if (competition.showLocation) {
            routeData.push(isNullOrUndefined(route.location) ? "" : route.location);

        }
        routeData.push("");
        routeData.push("");
        routeData.push("");

        return routeData;
    })
}

function convertImageToBase64(imageUrl) {
    const img = new Image();
    img.src = imageUrl;
    setTimeout(function() {
        // Whatever you want to do after the wait
    }, 1000);
    const canvas = document.createElement("canvas");
    canvas.width = img.width;
    canvas.height = img.height;
    const ctx = canvas.getContext("2d");
    if (!isNullOrUndefined(ctx)) {
        ctx.drawImage(img, 0, 0);
        const dataURL = canvas.toDataURL("image/png");
        console.log(dataURL);
        return dataURL;
    }
    return null;
}

const image = "data:image/png;base64,";